			<title>Tarea 1</title>
			<link rel="stylesheet" href="https://use.fontawesome.com/releases/v5.15.1/css/all.css" integrity="sha384-vp86vTRFVJgpjF9jiIGPEEqYqlDwgyBgEF109VFjmqGmIY/Y4HV4d3Gp2irVfcrp" crossorigin="anonymous"/>
		</Head>


    	<form action="#" className="form0" >
    		<fieldset className="form0_0_block">
			  	<p className="form0_0">
			  		<i className="fas fa-envelope fa-lg form0_0_icono"></i>
			  		<input type="text" placeholder="Correo electrónico"/>
			  	</p>
			  	<p className="form0_0">
			  		
			  		<i className="fas fa-lock fa-lg form0_0_icono"></i>
			  		<input type="text" placeholder="Contraseña"/>
			  	</p>
			  	<p className="form0_0">
			  		<input type="button" value="Ingresa" />
			  	</p>
		  	</fieldset>
    	</form>





		{/* Antes de la ultima etiqueta */}
		<style jsx>
			{`
				.wrapper{
					display:flex;
					justify-content:center;
					align-items:center;
					height:100vh;
					width:100vh;
					background-image: linear-gradient(to right top, #305b9f, #2359bf, #3651dc, #6040f1, #9000ff);
				}
				.form0{
					display:block;
					background:#ecf0f1;
					width:300px;
					padding:10px;
					height:400px;
					background-image: url("https://mdn.mozillademos.org/files/6457/mdn_logo_only_color.png");
				}
					.form0_0_block{
						height:380px;
						display:flex;
						flex-direction:column;
						
						
						background-size:100% 100%;
					}
					.form0_0{
						display:flex;
						flex-direction:row;
						justify-content:center;
						background:red;
					}
						i{
							margin-right:10px;
							background:orange;
							display:flex;
							align-items:center;
							
						}
						

			`}
		</style>

    </div>
  )
}
